import React, { Component } from 'react';

export default class Filter extends Component {
   state = {
      filter: 'all',
   };

   handleRadioChange = (e) => {
      this.setState({ filter: e.target.value });
   };

   render() {
      return (
         <div className="Filter">
            <input
               type="radio"
               name="filter"
               dataType="all"
               onChange={this.handleRadioChange}
            ></input>
            <label htmlFor="all">All</label>
            <input
               type="radio"
               name="filter"
               dataType="game"
               onChange={this.handleRadioChange}
            ></input>
            <label htmlFor="all">Games</label>
            <input
               type="radio"
               name="filter"
               dataType="movie"
               onChange={this.handleRadioChange}
            ></input>
            <label htmlFor="all">Movies</label>
            <input
               type="radio"
               name="filter"
               dataType="series"
               onChange={this.handleRadioChange}
            ></input>
            <label htmlFor="all">Series</label>
            <button onClick={this.props.filterMovies(this.state.filter)}>
               filter
            </button>
         </div>
      );
   }
}
