import React, { Component } from 'react'

export default class Filter extends Component {
    state = {
        filter: '',
    }

    handleSearchField = e => {
        this.setState({search:e.target.value})
    }

    handleKey = (event) =>{
        if(this.state.filter){
            this.props.filterMovies(this.state.filter)
        }
    }

    render() {
        return (
        <form className='Filter'>
            <input 
                type="radio"
                id="contactChoice3"
                name="contact" 
                value="mail">
            </input>
        </form>
        )
    }
}
