import React, { Component } from 'react';
import './Search.scss';

export default class Search extends Component {
   state = {
      search: '',
      filter: 'all',
   };

   handleSearchField = (e) => {
      this.setState({ search: e.target.value.replace(/ /g, '+') });
   };

   handleKey = (event) => {
      if (this.state.search) {
         if (event.key === 'Enter') {
            this.props.searchMovie(this.state.search);
         }
      }
   };

   handleSerchButton = () => {
      if (this.state.search) {
         this.props.searchMovie(this.state.search);
      }
   };

   handleFilter = (e) => {
      this.setState({ filter: e.target.dataset.type });
   };

   render() {
      return (
         <>
            <div className="Search">
               <input
                  className="Search-field"
                  type="text"
                  placeholder="search"
                  value={this.state.serch}
                  onChange={this.handleSearchField}
                  onKeyDown={this.handleKey}
               />
               <button onClick={this.handleSerchButton}>search</button>
            </div>
            <div className="Filter">
               <input
                  type="radio"
                  name="filter"
                  data-type="all"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">All</label>
               <input
                  type="radio"
                  name="filter"
                  data-type="game"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">Games</label>
               <input
                  type="radio"
                  name="filter"
                  data-type="movie"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">Movies</label>
               <input
                  type="radio"
                  name="filter"
                  data-type="series"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">Series</label>
            </div>
         </>
      );
   }
}
