import React, { Component } from 'react'

export default class Form extends Component {
    state={
        firstName:'',
        email: '',
        agreement: false

    }

    handleChange = (event) => {
        this.setState({[event.target.name]:event.target.value})
    }

    handleCheck = (event) =>{
        this.setState({[event.target.name]:event.target.checked})
    }

    validateName = () => {
        if(this.state.firstName.length < 5){
            alert('Name should be more than 5 symbols')
        }
    }

    validateEmail = () => {
        if(!/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(this.state.email)){
            return false
        } else{
            return true
        }
    }
    validateForm = () => {
        if(!this.validateEmail()){
            alert('incorrect email')
        } else if(!this.state.agreement){
            alert('agree with terms')
        } else{
            alert('good')
        }
    }

    render() {
        const {agreement,email} = this.state
        const  handleChange = this.handleChange
        const  handleCheck = this.handleCheck
        // const validateName = this.validateName
        // const validateEmail = this.validateEmail


        return (
        <div>
            <input 
                type="email"
                name='email'
                placeholder='Email'
                value={email}
                onChange={handleChange}
            />
            <br/>
            <label>
                <input 
                    type="checkbox"
                    name='agreement'
                    checked={agreement}
                    onChange={handleCheck}
                 />
                 I agree with terms {'&'} conditions
            </label>
            <br/>
            <input 
                type="submit"
                placeholder='Send'
                onSubmit={this.validateForm}
             />
        </div>
        )
    }
}
