import React, { Component } from 'react';
import './Search.scss';

export default class Search extends Component {
   state = {
      search: '',
      filter: 'all',
   };

   handleSearchField = (e) => {
      this.setState({ search: e.target.value.replace(/ /g, '+') });
   };

   handleKey = (event) => {
      if (this.state.search) {
         if (event.key === 'Enter') {
            this.props.searchMovie(this.state.search);
         }
      }
   };

   handleSerchButton = () => {
      if (this.state.search) {
         this.props.searchMovie(this.state.search);
      }
   };

   render() {
      return (
         <>
            <div className="Search">
               <input
                  className="Search-field"
                  type="text"
                  placeholder="search"
                  value={this.state.serch}
                  onChange={this.handleSearchField}
                  onKeyDown={this.handleKey}
               />
               <button onClick={this.handleSerchButton}>search</button>
            </div>
            <div className="Filter">
               <input
                  type="radio"
                  name="filter"
                  dataType="all"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">All</label>
               <input
                  type="radio"
                  name="filter"
                  dataType="game"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">Games</label>
               <input
                  type="radio"
                  name="filter"
                  dataType="movie"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">Movies</label>
               <input
                  type="radio"
                  name="filter"
                  dataType="series"
                  onChange={this.handleRadioChange}
               ></input>
               <label htmlFor="all">Series</label>
               <button
                  onClick={() => this.props.filterMovies(this.state.filter)}
               >
                  filter
               </button>
            </div>
         </>
      );
   }
}
