import React, { Component } from 'react'

export default class Timer extends Component {
    state={
        counter: 0
    }

    render() {
        return (
        <div>
            <h2>Timer</h2>
            <div>
                <h3>{this.state.counter}</h3>
                this.state.counter = 0 ? <button>Start</button> : <button>Stop</button>
            </div>
        </div>
        )
    }
}
