import React, { Component } from 'react'
import './Main.scss'
import MovieList from '../../components/MovieList/MovieList'
import Search from '../../components/Search/Search'
import Filter from '../../components/Filter/Filter'

export default class Main extends Component {

    state={
        movies: [],
        APIurl: 'http://www.omdbapi.com/?apikey=6874bd9f',
    }
    componentDidMount(){
        fetch(this.state.APIurl+`&s=spider+man`)
        .then(response => response.json())
        .then(data => this.setState({movies: data.Search}))
    }

    serchMovie = (search) => {
        this.setState({APIurl: this.state.APIurl+`&s=${search}`})
        console.log(this.state.APIurl)
        search.replace(/ /g, '+')
        fetch(this.state.APIurl+`&s=${search}`)
        .then(response => response.json())
        .then(data => this.setState({movies: data.Search}))
    }

    filterMovies = (filter) => {
        console.log(this.state.APIurl)
        filter.replace(/ /g, '+')
        fetch(this.state.APIurl + `&type=${filter}`)
        .then(response => response.json())
        .then(data => this.setState({movies: data.Search}))
    }

    render() {
        const {movies} = this.state

        return (
        <div className='Main'>
            <Search searchMovie={this.serchMovie}/>
            <Filter filterMovies={this.filterMovies}/>
            {
                movies.length ?
                <MovieList movies={movies}/> :
                <div className="lds-facebook"><div></div><div></div><div></div></div> //Preloaader https://loading.io/css/
            }
        </div>
        )
    }
}
