import React, { Component } from 'react';
import './Main.scss';
import MovieList from '../../components/MovieList/MovieList';
import Search from '../../components/Search/Search';

export default class Main extends Component {
   state = {
      movies: [],
      errorMessage: '',
   };
   componentDidMount() {
      fetch(`http://www.omdbapi.com/?apikey=6874bd9f&s=spider+man`)
         .then((response) => response.json())
         .then((data) => this.setState({ movies: data.Search }));
   }

   serchMovie = (search, filter) => {
      fetch(
         `http://www.omdbapi.com/?apikey=6874bd9f&s=${search}${
            filter !== 'all' ? `&type=${filter}` : ''
         }`
      )
         .then((response) => response.json())
         .then((data) => {
            if (data.Response === 'True') {
               this.setState({ movies: data.Search });
            } else if (data.Response === 'False') {
               this.setState({
                  errorMessage: `${data.Error} Please type more symbols`,
               });
               console.log(data.Error);
            }
         });
   };

   render() {
      const { movies } = this.state;

      return (
         <div className="Main">
            <p>{this.state.errorMessage}</p>
            <Search searchMovie={this.serchMovie} />
            {
               movies.length ? (
                  <MovieList movies={movies} />
               ) : (
                  <div className="lds-facebook">
                     <div></div>
                     <div></div>
                     <div></div>
                  </div>
               ) //Preloaader https://loading.io/css/
            }
         </div>
      );
   }
}
